mapper.select:
SELECT * 
FROM gboardlist 
WHERE bhobby = #{bhobby} AND mhobby = #{mhobby} AND gender = #{gender} AND city = #{city} AND area = #{area} AND meeting_date = #{meeting_date}

mapper.update:
UPDATE gboardlist SET 
	subject = #{subject}
	,content = #{content}
	,bhobby = #{bhobby}
	,mhobby = #{mhobby}
	,minage = #{minage}
	,maxage = #{maxage}
	,gender = #{gender}
	,city = #{city}
	,area = #{area}
	,meeting_date = #{meeting_date}
	,maxmem = #{maxmem}
	,mannertp = #{mannertp}
WHERE gidx = #{idx}
